import React, { useState, useEffect, useCallback } from "react";
import { motion, AnimatePresence } from "framer-motion";
import PropTypes from 'prop-types';
import "../styles/MagicalQuestionCard.css";

const MagicalQuestionCard = ({ question = "Default Question", triggerEffect }) => {
  const [particles, setParticles] = useState([]);
  const [isIntegrating, setIsIntegrating] = useState(true);

  const generateParticles = useCallback(() => {
    const particlesArray = [];
    // Create a denser grid for more particles
    const gridSize = { x: 50, y: 40 };

    for (let i = 0; i < gridSize.x; i++) {
      for (let j = 0; j < gridSize.y; j++) {
        const baseX = (i / gridSize.x) * 100;
        const baseY = (j / gridSize.y) * 100;

        particlesArray.push({
          id: `particle-${i}-${j}`,
          initialX: baseX,
          initialY: baseY,
          // More dramatic scatter effect
          finalX: baseX + (Math.random() - 0.5) * 300,
          finalY: baseY + (Math.random() - 0.5) * 300,
          size: Math.random() * 3 + 1,
          delay: Math.random() * 0.5,
          rotation: Math.random() * 360,
        });
      }
    }
    return particlesArray;
  }, []);

  useEffect(() => {
    setIsIntegrating(true);
    const newParticles = generateParticles();
    setParticles(newParticles);

    const timer = setTimeout(() => {
      setIsIntegrating(false);
    }, 1500);

    return () => clearTimeout(timer);
  }, [triggerEffect, generateParticles]);

  return (
    <div className="card-wrapper">
      <div className="thanos-card-container">
        {/* Card Background */}
        <motion.div 
          className="card-base"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.8, duration: 0.5 }}
        >
          {/* Question Text */}
          <motion.div 
            className="question-content"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 1, duration: 0.5 }}
          >
            <h2>{question}</h2>
          </motion.div>
        </motion.div>

        {/* Particles Effect */}
        <div className="particles-container">
          {particles.map((particle) => (
            <motion.div
              key={particle.id}
              className="thanos-particle"
              initial={{
                x: isIntegrating ? particle.finalX + '%' : particle.initialX + '%',
                y: isIntegrating ? particle.finalY + '%' : particle.initialY + '%',
                opacity: isIntegrating ? 0 : 1,
                scale: isIntegrating ? 0 : 1,
                rotate: particle.rotation
              }}
              animate={{
                x: isIntegrating ? particle.initialX + '%' : particle.finalX + '%',
                y: isIntegrating ? particle.initialY + '%' : particle.finalY + '%',
                opacity: isIntegrating ? 1 : 0,
                scale: isIntegrating ? 1 : 0
              }}
              transition={{
                duration: 1,
                delay: particle.delay,
                ease: [0.4, 0, 0.2, 1]
              }}
              style={{
                width: `${particle.size}px`,
                height: `${particle.size}px`,
              }}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

MagicalQuestionCard.propTypes = {
  question: PropTypes.string,
  triggerEffect: PropTypes.number
};

export default MagicalQuestionCard;
