import React, { useState } from 'react';
import MagicalQuestionCard from './MagicalQuestionCard';
import '../styles/Game.css';

const Game = () => {
  const [currentQuestion, setCurrentQuestion] = useState("What is the capital of France?");
  const [triggerEffect, setTriggerEffect] = useState(0);

  const handleNextQuestion = () => {
    setTriggerEffect(prev => prev + 1);

    // Update question after particles have flown away and before they come back
    setTimeout(() => {
      setCurrentQuestion("What is the capital of Spain?"); // Your question logic here
    }, 750); // Adjusted timing to be in the middle of the animation
  };

  return (
    <div className="game-container">
      <MagicalQuestionCard 
        question={currentQuestion}
        triggerEffect={triggerEffect}
      />
      <button 
        className="next-button"
        onClick={handleNextQuestion}
      >
        Next Question
      </button>
    </div>
  );
};

export default Game;
