import React, { useState } from "react";
import MagicalQuestionCard from "./MagicalQuestionCard";

const questions = [
  "What is the capital of France?",
  "What is 2 + 2?",
  "Who wrote 'Hamlet'?",
  "What is the speed of light?",
];

const GamePresenter = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [triggerEffect, setTriggerEffect] = useState(0);

  const nextQuestion = () => {
    setTriggerEffect((prev) => prev + 1); // Update triggerEffect to trigger the effect immediately
    setCurrentIndex((prev) => (prev + 1) % questions.length);
  };

  return (
    <div>
      <h1 style={{ textAlign: "center" }}>Welcome to the Game!</h1>
      <MagicalQuestionCard question={questions[currentIndex]} triggerEffect={triggerEffect} />
      <div style={{ textAlign: "center", marginTop: "20px" }}>
        <button onClick={nextQuestion} className="next-btn">
          Next Question
        </button>
      </div>
    </div>
  );
};

export default GamePresenter;
