import React, { useState, useEffect } from "react";
import "../styles/MagicalQuestionCard.css";

const generateDustParticles = () => {
  const particles = [];
  for (let i = 0; i < 80; i++) {
    const size = Math.random() * 10 + 5; // Particle size between 5px and 15px
    const randomX = Math.random() * 100; // X position
    const randomY = Math.random() * 100; // Y position
    const delay = Math.random() * 1.5; // Random animation delay

    particles.push(
      <div
        key={i}
        className="dust-particle"
        style={{
          left: `${randomX}%`,
          top: `${randomY}%`,
          width: `${size}px`,
          height: `${size}px`,
          animationDelay: `${delay}s`,
        }}
      ></div>
    );
  }
  return particles;
};

const MagicalQuestionCard = ({ question = "Default Question", key }) => {
  const [dustParticles, setDustParticles] = useState(generateDustParticles());
  const [show, setShow] = useState(false);

  useEffect(() => {
    if (question) {
      setShow(true);
      setDustParticles(generateDustParticles());
     // setTimeout(() => setShow(false), 5000); // Optional: hide after 5 seconds
    }
  }, [question]);

  return (
    <div className={`card-container ${show ? "show" : ""}`}>
      <div className="dust-field">{dustParticles}</div>
      <div className="card-content">
        <h2 className="question-text">{question}</h2>

      </div>
    </div>
  );
};

export default MagicalQuestionCard;
