import React, { useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import '../styles/MagicalQuestionCard.css';

const MagicalQuestionCard = ({ message, triggerEffect }) => {
  const cardRef = useRef(null);

  useEffect(() => {
    // Add a small delay to ensure the card is mounted
    const timer = setTimeout(() => {
      if (cardRef.current) {
        createParticles(cardRef.current);
      }
    }, 100);

    return () => clearTimeout(timer);
  }, [message]); // Remove triggerEffect from dependencies

  const createParticles = (element) => {
    // Clear existing particles
    const existingWrapper = element.querySelector('.particles-wrapper');
    if (existingWrapper) {
      existingWrapper.remove();
    }

    // Create particles wrapper
    const particlesWrapper = document.createElement('div');
    particlesWrapper.className = 'particles-wrapper';
    particlesWrapper.style.position = 'absolute';
    particlesWrapper.style.width = '100%';
    particlesWrapper.style.height = '100%';
    particlesWrapper.style.overflow = 'hidden';
    particlesWrapper.style.pointerEvents = 'none'; // Ensure particles don't interfere with interactions

    // Get the card dimensions
    const cardBounds = element.getBoundingClientRect();

    // Use smaller offsets that match the card's padding
    const offsetX = 20;
    const offsetY = 20;

    // Create new particles with better distribution
    for (let i = 0; i < 3000; i++) {
      const particle = document.createElement('div');
      particle.className = 'thanos-particle';

      // Keep particles within the card bounds
      const x = Math.random() * (cardBounds.width - 2 * offsetX) + offsetX;
      const y = Math.random() * (cardBounds.height - 2 * offsetY) + offsetY;

      // Set particle size
      const size = Math.random() * 4 + 2;
      particle.style.width = `${size}px`;
      particle.style.height = `${size}px`;

      // Position relative to the card
      particle.style.left = `${x}px`;
      particle.style.top = `${y}px`;
      particle.style.position = 'absolute';

      // Calculate initial positions (where particles come from)
      const angle = Math.random() * Math.PI * 2;
      const distance = 150 + Math.random() * 100;
      const tx = Math.cos(angle) * distance;
      const ty = Math.sin(angle) * distance;

      // Set transform origin to center of particle
      particle.style.transformOrigin = 'center center';
      particle.style.setProperty('--tx', `${tx}px`);
      particle.style.setProperty('--ty', `${ty}px`);

      // Adjusted timing for smoother effect
      const inDelay = Math.random() * 0.3;
      const holdDelay = 1.8;
      const outDelay = holdDelay + Math.random() * 0.2;

      particle.style.animation = `
        particleAnimationIn 1.2s cubic-bezier(0.4, 0, 0.2, 1) ${inDelay}s forwards,
        particleAnimationOut 1.2s cubic-bezier(0.4, 0, 0.2, 1) ${outDelay}s forwards
      `;

      particlesWrapper.appendChild(particle);
    }

    // Insert the particles wrapper as the first child of the card
    element.insertBefore(particlesWrapper, element.firstChild);
  };

  return (
    <AnimatePresence>
      {message && (
        <motion.div 
          ref={cardRef}
          className="magical-card"
          key={message}
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          exit={{ opacity: 0, scale: 0.8 }}
          transition={{ duration: 0.5 }}
        >
          <div className="card-content">
            {message || "Waiting for the game to begin..."}
          </div>
        </motion.div>
      )}
    </AnimatePresence>
  );
};

export default MagicalQuestionCard;
