import React, { useState, useEffect } from "react";
import "../styles/MagicalQuestionCard.css";


const generateDustParticles = () => {
  const particles = [];
  for (let i = 0; i < 100; i++) {
    const size = Math.random() * 8 + 3; // Particles are between 3px to 11px
    const randomX = Math.random() * 100; // X position (from 0% to 100%)
    const randomY = Math.random() * 100; // Y position (from 0% to 100%)
    const delay = Math.random() * 2; // Each particle has a different delay for randomness

    particles.push(
      <div
        key={i}
        className="dust-particle"
        style={{
          left: `${randomX}%`,
          top: `${randomY}%`,
          width: `${size}px`,
          height: `${size}px`,
          animationDelay: `${delay}s`,
        }}
      ></div>
    );
  }
  return particles;
};

const MagicalQuestionCard = ({ question }) => {
  const [show, setShow] = useState(false);

  useEffect(() => {
    if (question) {
      setShow(true);
      setTimeout(() => setShow(false), 5000); // Hide after 5 seconds if needed
    }
  }, [question]);

  return (
    <div className={`card-container ${show ? "show" : ""}`}>
      <div className="dust-field">{show && generateDustParticles()}</div>
      <div className="card-content">
        <h2 className="question-text">{question}</h2>
      </div>
    </div>
  );
};

export default MagicalQuestionCard;
