import React, { useState, useEffect } from 'react';
import { motion } from 'framer-motion';
import ErrorBoundary from './ErrorBoundary';
import '../styles/Game.css';

// Debug logger
const DEBUG = process.env.REACT_APP_DEBUG_MODE === 'true';
const debugLog = (component, action, data) => {
  if (DEBUG) {
    console.log(`[${component}][${action}]`, data);
  }
};

const GamePresenter = () => {
  const [difficulty, setDifficulty] = useState('MEDIUM');
  const [numberOfRounds, setNumberOfRounds] = useState(5);
  const [gameStarted, setGameStarted] = useState(false);
  const [componentMounted, setComponentMounted] = useState(false);

  // Component mount debugging
  useEffect(() => {
    debugLog('GamePresenter', 'Mount', { initial: true });
    setComponentMounted(true);

    return () => {
      debugLog('GamePresenter', 'Unmount', { cleanup: true });
    };
  }, []);

  // State change debugging
  useEffect(() => {
    debugLog('GamePresenter', 'StateUpdate', {
      difficulty,
      numberOfRounds,
      gameStarted,
      componentMounted
    });
  }, [difficulty, numberOfRounds, gameStarted, componentMounted]);

  const handleDifficultyChange = (level) => {
    debugLog('GamePresenter', 'DifficultyChange', { from: difficulty, to: level });
    setDifficulty(level);
  };

  const handleRoundsChange = (value) => {
    debugLog('GamePresenter', 'RoundsChange', { from: numberOfRounds, to: value });
    setNumberOfRounds(Number(value));
  };

  const handleStartGame = () => {
    console.log('Starting game...'); // Debug log
    setGameStarted(true);
    // Add any additional game start logic here
  };

  return (
    <ErrorBoundary>
      <div className="game-container">
        <div className="game-setup">
          <h1 className="game-title">Legendary Lines Game</h1>

          <div className="difficulty-selector">
            <h3>Select Difficulty:</h3>
            <div className="difficulty-buttons">
              {['EASY', 'MEDIUM', 'HARD'].map((level) => (
                <motion.button
                  key={level}
                  className={`difficulty-btn ${difficulty === level ? 'selected' : ''}`}
                  onClick={() => handleDifficultyChange(level)}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  {level}
                  <span className="multiplier">
                    {level === 'EASY' ? '1x' : level === 'MEDIUM' ? '1.5x' : '2x'} points
                  </span>
                </motion.button>
              ))}
            </div>
          </div>

          <div className="rounds-selector">
            <h3>Number of Rounds:</h3>
            <input
              type="number"
              value={numberOfRounds}
              onChange={(e) => handleRoundsChange(e.target.value)}
              min="1"
              max="10"
              className="rounds-input"
            />
          </div>

          <button 
            className="start-btn"
            onClick={handleStartGame}
            type="button" // Explicitly set button type
          >
            Start Game
          </button>

          <div className="game-stats">
            <div className="streak-counter">Streak: 0</div>
            <div className="current-difficulty">Level: {difficulty}</div>
          </div>
        </div>
      </div>
    </ErrorBoundary>
  );
};

export default GamePresenter;
