import React, { useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import confetti from 'canvas-confetti';
import '../styles/MagicalQuestionCard.css';

const MagicalQuestionCard = ({ message, triggerEffect, isRoundComplete }) => {
  const cardRef = useRef(null);

  useEffect(() => {
    if (cardRef.current) {
      createParticles(cardRef.current);
    }
  }, [message, triggerEffect]);

  // Add new useEffect for confetti
  useEffect(() => {
    if (isRoundComplete) {
      fireCelebrationConfetti();
    }
  }, [isRoundComplete]);

  const fireCelebrationConfetti = () => {
    // First burst
    const count = 200;
    const defaults = {
      origin: { y: 0.7 },
      zIndex: 9999
    };

    function fire(particleRatio, opts) {
      confetti({
        ...defaults,
        ...opts,
        particleCount: Math.floor(count * particleRatio),
      });
    }

    // Start the sequence
    fire(0.25, {
      spread: 26,
      startVelocity: 55,
      decay: 0.95,
      scalar: 0.8
    });

    fire(0.2, {
      spread: 60,
      decay: 0.91,
      scalar: 1.2
    });

    fire(0.35, {
      spread: 100,
      decay: 0.92,
      scalar: 1.2,
      startVelocity: 45
    });

    fire(0.1, {
      spread: 120,
      startVelocity: 25,
      decay: 0.92,
      scalar: 1.3
    });

    fire(0.1, {
      spread: 120,
      startVelocity: 45,
      decay: 0.91,
      scalar: 1.1
    });

    // Add a delayed second burst for extra celebration
    setTimeout(() => {
      fire(0.25, {
        spread: 100,
        startVelocity: 45,
        decay: 0.92,
        scalar: 1.2
      });
    }, 500);
  };

  const createParticles = (element) => {
    // Clear existing particles
    const existingWrapper = element.querySelector('.particles-wrapper');
    if (existingWrapper) {
      existingWrapper.remove();
    }

    // Create particles wrapper
    const particlesWrapper = document.createElement('div');
    particlesWrapper.className = 'particles-wrapper';

    // Get the card content element
    const cardContent = element.querySelector('.card-content');
    const elementBounds = element.getBoundingClientRect();

    // Create new particles with better distribution
    for (let i = 0; i < 2000; i++) {
      const particle = document.createElement('div');
      particle.className = 'thanos-particle';

      // Calculate position within the card bounds
      const padding = 20;
      const x = padding + (Math.random() * (elementBounds.width - 2 * padding));
      const y = padding + (Math.random() * (elementBounds.height - 2 * padding));

      // Particle size
      const size = Math.random() * 4 + 2;
      particle.style.width = `${size}px`;
      particle.style.height = `${size}px`;

      // Set initial position
      particle.style.left = `${x}px`;
      particle.style.top = `${y}px`;

      // Set animation properties
      const angle = Math.random() * Math.PI * 2;
      const distance = 200 + Math.random() * 100;
      const tx = Math.cos(angle) * distance;
      const ty = Math.sin(angle) * distance;
      particle.style.setProperty('--tx', `${tx}px`);
      particle.style.setProperty('--ty', `${ty}px`);

      // Animation timing
      const inDelay = Math.random() * 0.3;
      const holdDelay = 1.5;
      const outDelay = holdDelay + Math.random() * 0.3;

      particle.style.animation = `
        particleAnimationIn 1s cubic-bezier(0.4, 0, 0.2, 1) ${inDelay}s forwards,
        particleAnimationOut 1s cubic-bezier(0.4, 0, 0.2, 1) ${outDelay}s forwards
      `;

      particlesWrapper.appendChild(particle);
    }

    // Insert the particles wrapper as the first child of the element
    element.insertBefore(particlesWrapper, element.firstChild);
  };

  function handleSubmit(event) {
    event.preventDefault();
    const inputValue = event.target.elements.inputName.value; // Ensure this is correct
    // Check if inputValue is being sent correctly
    if (inputValue) {
        // Send inputValue to the AI
        sendToAI(inputValue);
    } else {
        console.error("Input value is empty");
    }
  }

  return (
    <AnimatePresence mode="wait">
      <motion.div 
        ref={cardRef}
        className="magical-card"
        key={triggerEffect}
        initial={{ opacity: 0, scale: 0.8 }}
        animate={{ opacity: 1, scale: 1 }}
        exit={{ opacity: 0, scale: 0.8 }}
        transition={{ duration: 0.5 }}
      >
        <div className="card-content">
          {message || "Waiting for the game to begin..."}
        </div>
      </motion.div>
    </AnimatePresence>
  );
};

export default MagicalQuestionCard;
