import React, { useState, useEffect, useCallback } from "react";
import { motion, AnimatePresence } from "framer-motion";
import PropTypes from 'prop-types';
import "../styles/MagicalQuestionCard.css";

const MagicalQuestionCard = ({ question = "Default Question", triggerEffect }) => {
  const [dustParticles, setDustParticles] = useState([]);
  const [showDust, setShowDust] = useState(false);
  const [isFlipping, setIsFlipping] = useState(false);

  const generateDustParticles = useCallback(() => {
    return Array.from({ length: 100 }, (_, i) => ({
      id: i,
      size: Math.random() * 12 + 3,
      x: Math.random() * 100,
      y: Math.random() * 100,
      delay: Math.random() * 2,
      color: `hsl(${Math.random() * 360}, 80%, 60%)`
    }));
  }, []);

  useEffect(() => {
    setIsFlipping(true);
    setShowDust(true);
    setDustParticles(generateDustParticles());

    const timer = setTimeout(() => {
      setShowDust(false);
      setIsFlipping(false);
    }, 2000);

    return () => clearTimeout(timer);
  }, [question, triggerEffect, generateDustParticles]);

  return (
    <AnimatePresence mode="wait">
      <motion.div 
        className={`magical-card-container ${isFlipping ? 'flipping' : ''}`}
        initial={{ rotateY: -180, opacity: 0 }}
        animate={{ rotateY: 0, opacity: 1 }}
        exit={{ rotateY: 180, opacity: 0 }}
        transition={{ duration: 0.6, ease: "easeOut" }}
      >
        <motion.div 
          className="card-content"
          animate={{
            scale: [1, 1.02, 1],
            boxShadow: [
              "0 4px 8px rgba(0,0,0,0.1)",
              "0 8px 16px rgba(0,0,0,0.2)",
              "0 4px 8px rgba(0,0,0,0.1)"
            ]
          }}
          transition={{ duration: 1, ease: "easeInOut", times: [0, 0.5, 1] }}
        >
          <motion.h2 
            className="question-text"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: 0.3 }}
          >
            {question}
          </motion.h2>

          {showDust && (
            <div className="magical-effects">
              {dustParticles.map(particle => (
                <motion.div
                  key={particle.id}
                  className="magical-particle"
                  initial={{ 
                    scale: 0,
                    opacity: 0,
                    x: 0,
                    y: 0
                  }}
                  animate={{ 
                    scale: [0, 1, 0],
                    opacity: [0, 0.8, 0],
                    x: [0, (Math.random() - 0.5) * 200],
                    y: [0, (Math.random() - 0.5) * 200]
                  }}
                  transition={{
                    duration: 2,
                    delay: particle.delay,
                    ease: "easeOut"
                  }}
                  style={{
                    width: `${particle.size}px`,
                    height: `${particle.size}px`,
                    background: particle.color,
                    left: `${particle.x}%`,
                    top: `${particle.y}%`,
                  }}
                />
              ))}
            </div>
          )}

          <div className="card-glow" />
        </motion.div>
      </motion.div>
    </AnimatePresence>
  );
};

MagicalQuestionCard.propTypes = {
  question: PropTypes.string,
  triggerEffect: PropTypes.number
};

export default MagicalQuestionCard;
