import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import PropTypes from 'prop-types';
import "../styles/MagicalQuestionCard.css";

const MagicalQuestionCard = ({ question = "Default Question", triggerEffect }) => {
  const [particles, setParticles] = useState([]);
  const [showParticles, setShowParticles] = useState(true);

  const generateParticles = () => {
    const particlesArray = [];
    const cardElement = document.querySelector('.card-content');
    if (!cardElement) return [];

    const cardRect = cardElement.getBoundingClientRect();
    const cardWidth = cardRect.width;
    const cardHeight = cardRect.height;

    const columns = 60;
    const rows = 30;

    for (let i = 0; i < columns; i++) {
      for (let j = 0; j < rows; j++) {
        const baseX = (i / columns) * cardWidth;
        const baseY = (j / rows) * cardHeight;

        particlesArray.push({
          id: `particle-${i}-${j}-${triggerEffect}`,
          initialX: baseX,
          initialY: baseY,
          finalX: baseX + (Math.random() - 0.5) * cardWidth * 2,
          finalY: baseY + (Math.random() - 0.5) * cardHeight * 2,
          size: Math.random() * 2 + 1,
          delay: (j / rows) * 0.5 + Math.random() * 0.3,
          rotation: Math.random() * 360
        });
      }
    }
    return particlesArray;
  };

  useEffect(() => {
    setParticles(generateParticles());
    setShowParticles(true);

    const hideParticlesTimer = setTimeout(() => {
      setShowParticles(false);
    }, 2000); // Hide particles after 2 seconds

    return () => clearTimeout(hideParticlesTimer);
  }, []);

  useEffect(() => {
    if (triggerEffect > 0) {
      setShowParticles(false);

      const timer = setTimeout(() => {
        setParticles(generateParticles());
        setShowParticles(true);

        const hideParticlesTimer = setTimeout(() => {
          setShowParticles(false);
        }, 2000); // Hide particles after 2 seconds

        return () => clearTimeout(hideParticlesTimer);
      }, 0); // Trigger immediately

      return () => clearTimeout(timer);
    }
  }, [triggerEffect]);

  return (
    <div className="card-container">
      <div className="card-wrapper">
        <motion.div 
          className="card-content"
          key={`content-${triggerEffect}`}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          <motion.h2 
            className="question-text"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.5 }}
          >
            {question}
          </motion.h2>
        </motion.div>

        <div className="particles-wrapper">
          <AnimatePresence>
            {particles.map((particle) => (
              <motion.div
                key={particle.id}
                className="thanos-particle"
                initial={{
                  x: showParticles ? particle.finalX : particle.initialX,
                  y: showParticles ? particle.finalY : particle.initialY,
                  opacity: 0,
                  scale: 0,
                  rotate: particle.rotation
                }}
                animate={{
                  x: showParticles ? particle.initialX : particle.finalX,
                  y: showParticles ? particle.initialY : particle.finalY,
                  opacity: showParticles ? 0.8 : 0,
                  scale: showParticles ? 1 : 0,
                  rotate: showParticles ? 0 : particle.rotation
                }}
                exit={{
                  opacity: 0,
                  scale: 0
                }}
                transition={{
                  duration: 1.5,
                  delay: particle.delay,
                  ease: [0.23, 1, 0.32, 1]
                }}
                style={{
                  width: `${particle.size}px`,
                  height: `${particle.size}px`,
                  position: 'absolute',
                  left: 0,
                  top: 0,
                  background: '#FF9F1C',
                  borderRadius: '50%',
                  boxShadow: '0 0 4px rgba(255, 159, 28, 0.6)',
                  zIndex: 2
                }}
              />
            ))}
          </AnimatePresence>
        </div>
      </div>
    </div>
  );
};

MagicalQuestionCard.propTypes = {
  question: PropTypes.string,
  triggerEffect: PropTypes.number
};

export default MagicalQuestionCard;
