import React, { useState, useEffect } from "react";
import "../styles/MagicalQuestionCard.css";

const generateDustParticles = () => {
  const particles = [];
  for (let i = 0; i < 80; i++) {
    const size = Math.random() * 10 + 5;
    const randomX = Math.random() * 200 - 50; // Range: -50% to 150%
    const randomY = Math.random() * 200 - 50; // Range: -50% to 150%
    const delay = Math.random() * 1.5;

    particles.push(
      <div
        key={i}
        className="dust-particle"
        style={{
          left: `${randomX}%`,
          top: `${randomY}%`,
          width: `${size}px`,
          height: `${size}px`,
          animationDelay: `${delay}s`,
        }}
      ></div>
    );
  }
  return particles;
};

const MagicalQuestionCard = ({ question }) => {
  const [showParticles, setShowParticles] = useState(false);

  useEffect(() => {
    // Force a re-render of the dust animation
    setShowParticles(false); // Hide dust particles first
    const timeoutId = setTimeout(() => setShowParticles(true), 100); // Delay to make particles reappear

    return () => clearTimeout(timeoutId); // Clean up the timeout to avoid memory leaks
  }, [question]); // Trigger effect whenever `question` changes

  return (
    <div className="card-wrapper">
      <div className="card-container show">
        <div className="dust-field">{showParticles && generateDustParticles()}</div>
        <div className="card-content">
          <h2 className="question-text">{question}</h2>
        </div>
      </div>
    </div>
  );
};

export default MagicalQuestionCard;
