import React, { useState } from "react";
import "../styles/MagicalQuestionCard.css";

const generateDustParticles = () => {
  const particles = [];
  for (let i = 0; i < 80; i++) {
    const size = Math.random() * 10 + 5;
    const randomX = Math.random() * 200 - 50; // Range: -50% to 150%
    const randomY = Math.random() * 200 - 50; // Range: -50% to 150%
    const delay = Math.random() * 1.5;

    particles.push(
      <div
        key={i}
        className="dust-particle"
        style={{
          left: `${randomX}%`,
          top: `${randomY}%`,
          width: `${size}px`,
          height: `${size}px`,
          animationDelay: `${delay}s`,
        }}
      ></div>
    );
  }
  return particles;
};

const MagicalQuestionCard = ({ question = "Default Question" }) => {
  const [animationKey, setAnimationKey] = useState(0);

  const handleNextClick = () => {
    // Increment the key to force re-render of particles
    setAnimationKey((prevKey) => prevKey + 1);
  };

  return (
    <div className="card-wrapper">
      <div className="card-container show">
        <div className="dust-field" key={animationKey}>
          {generateDustParticles()}
        </div>
        <div className="card-content">
          <h2 className="question-text">{question}</h2>
          <button className="next-button" onClick={handleNextClick}>
            Next
          </button>
        </div>
      </div>
    </div>
  );
};

export default MagicalQuestionCard;
