import React, { useState, useEffect } from "react";
import "../styles/MagicalQuestionCard.css";

const generateStars = () => {
  const stars = [];
  for (let i = 0; i < 60; i++) {
    const isBigStar = Math.random() > 0.8; // 20% chance to be a big star
    const size = isBigStar ? 15 : 8; // Big stars are 15px, small are 8px
    const randomX = Math.random() * 100; // X position
    const randomY = Math.random() * 100; // Y position
    const delay = Math.random() * 2; // Random delay

    stars.push(
      <div
        key={i}
        className={`star ${isBigStar ? "big-star" : "small-star"}`}
        style={{
          left: `${randomX}%`,
          top: `${randomY}%`,
          width: `${size}px`,
          height: `${size}px`,
          animationDelay: `${delay}s`,
        }}
      ></div>
    );
  }
  return stars;
};

const MagicalQuestionCard = ({ question }) => {
  const [show, setShow] = useState(false);

  useEffect(() => {
    if (question) {
      setShow(true);
      setTimeout(() => setShow(false), 5000); // Optionally hide after 5 seconds
    }
  }, [question]);

  return (
    <div className={`card-container ${show ? "show" : ""}`}>
      <div className="star-field">{show && generateStars()}</div>
      <div className="card-content">
        <h2 className="question-text">{question}</h2>
      </div>
    </div>
  );
};

export default MagicalQuestionCard;
