import React, { useState, useEffect } from "react";
import "../styles/MagicalQuestionCard.css";

const generateDustParticles = () => {
  const particles = [];
  for (let i = 0; i < 80; i++) {
    const size = Math.random() * 10 + 5; // Particle size between 5px and 15px
    const randomX = Math.random() * 100; // X position
    const randomY = Math.random() * 100; // Y position
    const delay = Math.random() * 1.5; // Random animation delay

    particles.push(
      <div
        key={i}
        className="dust-particle"
        style={{
          left: `${randomX}%`,
          top: `${randomY}%`,
          width: `${size}px`,
          height: `${size}px`,
          animationDelay: `${delay}s`,
        }}
      ></div>
    );
  }
  return particles;
};

const MagicalQuestionCard = ({ question = "Default Question", triggerEffect }) => {
  const [dustParticles, setDustParticles] = useState([]);
  const [showDust, setShowDust] = useState(false);

  useEffect(() => {
    setShowDust(true);
    setDustParticles(generateDustParticles());
    const timer = setTimeout(() => setShowDust(false), 5000); // Dust disappears after 2 seconds
    return () => clearTimeout(timer);
  }, [question, triggerEffect]);

  return (
    <div className="card-container">
      <div className="card-content">
        <h2 className="question-text">{question}</h2>
      </div>
      {showDust && <div className="dust-field">{dustParticles}</div>}
    </div>
  );
};

export default MagicalQuestionCard;
