import React, { useRef, useEffect, useState } from 'react';
import styled from 'styled-components';

const CardContainer = styled.div`
  position: relative;
  width: 300px;
  height: 200px;
`;

const Card = styled.div`
  background-color: white;
  border-radius: 10px;
  padding: 20px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  opacity: ${props => props.visible ? 1 : 0};
  transition: opacity 0.5s ease-in;
`;

const AnimatedAppearance = ({ children, duration = 2000, particleCount = 1000 }) => {
  const canvasRef = useRef(null);
  const [cardVisible, setCardVisible] = useState(false);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    canvas.width = 300;
    canvas.height = 200;

    const particles = Array(particleCount).fill().map(() => ({
      x: Math.random() * canvas.width,
      y: Math.random() * canvas.height,
      size: Math.random() * 3 + 1,
      speedX: (Math.random() - 0.5) * 3,
      speedY: (Math.random() - 0.5) * 3,
    }));

    let animationFrame;
    const startTime = performance.now();

    const animate = (currentTime) => {
      const progress = (currentTime - startTime) / duration;
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      particles.forEach((particle) => {
        const targetX = canvas.width / 2;
        const targetY = canvas.height / 2;

        particle.x += (targetX - particle.x) * 0.05;
        particle.y += (targetY - particle.y) * 0.05;

        ctx.fillStyle = `rgba(240, 240, 240, ${1 - progress})`;
        ctx.beginPath();
        ctx.arc(particle.x, particle.y, particle.size * (1 - progress), 0, Math.PI * 2);
        ctx.fill();
      });

      if (progress < 1) {
        animationFrame = requestAnimationFrame(animate);
      } else {
        setCardVisible(true);
      }
    };

    animationFrame = requestAnimationFrame(animate);

    return () => cancelAnimationFrame(animationFrame);
  }, [duration, particleCount]);

  return (
    <CardContainer>
      <canvas ref={canvasRef} style={{ position: 'absolute', top: 0, left: 0 }} />
      <Card visible={cardVisible}>{children}</Card>
    </CardContainer>
  );
};

const QuestionCard = ({ question, animationDuration, particleCount }) => (
  <AnimatedAppearance duration={animationDuration} particleCount={particleCount}>
    <h3>{question}</h3>
  </AnimatedAppearance>
);

export default QuestionCard;
