import React, { useEffect, useRef, useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import '../styles/MagicalQuestionCard.css';

const CardContainer = ({ message, showWelcome }) => {
  const cardRef = useRef(null);

  useEffect(() => {
    if (cardRef.current) {
      createParticles(cardRef.current);
    }
  }, [message, showWelcome]);

  const createParticles = (element) => {
    // Clear existing particles
    const existingWrapper = element.querySelector('.particles-wrapper');
    if (existingWrapper) {
      existingWrapper.remove();
    }

    // Create particles wrapper
    const particlesWrapper = document.createElement('div');
    particlesWrapper.className = 'particles-wrapper';

    // Get the card content element
    const cardContent = element.querySelector('.card-content');
    const elementBounds = element.getBoundingClientRect();
    const contentBounds = cardContent.getBoundingClientRect();

    // Calculate the actual card area relative to the parent
    const cardTop = contentBounds.top - elementBounds.top;
    const cardLeft = contentBounds.left - elementBounds.left;

    // Create new particles with better distribution
    for (let i = 0; i < 3000; i++) { // Increased particles for better coverage
      const particle = document.createElement('div');
      particle.className = 'thanos-particle';

      // Distribute particles within the actual card area
      const x = cardLeft + (Math.random() * contentBounds.width + 30 );
      const y = cardTop + (Math.random() * contentBounds.height + 20);

      // Slightly larger particles
      const size = Math.random() * 5 + 1; // 2-7px size range
      particle.style.width = `${size}px`;
      particle.style.height = `${size}px`;

      // Position relative to the card
      particle.style.left = `${x}px`;
      particle.style.top = `${y}px`;

      // Wider spread for initial positions
      const angle = Math.random() * Math.PI * 2;
      const distance = 300 + Math.random() * 200; // Much wider spread
      const tx = Math.cos(angle) * distance;
      const ty = Math.sin(angle) * distance;
      particle.style.setProperty('--tx', `${tx}px`);
      particle.style.setProperty('--ty', `${ty}px`);

      // Adjusted timing for smoother effect
      const inDelay = Math.random() * 0.5; // Shorter delay for faster start
      const holdDelay = 1.8; // Longer hold time
      const outDelay = holdDelay + Math.random() * 0.2;

      particle.style.animation = `
        particleAnimationIn 1.2s cubic-bezier(0.4, 0, 0.2, 1) ${inDelay}s forwards,
        particleAnimationOut 1.2s cubic-bezier(0.4, 0, 0.2, 1) ${outDelay}s forwards
      `;

      particlesWrapper.appendChild(particle);
    }

    element.insertBefore(particlesWrapper, element.firstChild);
  };

  function handleSubmit(event) {
    event.preventDefault();
    const inputValue = event.target.elements.inputName.value; // Ensure this is correct
    // Check if inputValue is being sent correctly
    if (inputValue) {
        // Send inputValue to the AI
        sendToAI(inputValue);
    } else {
        console.error("Input value is empty");
    }
  }

  return (
    <AnimatePresence>
      {showWelcome ? (
        <motion.div
          key="welcome"
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          exit={{ opacity: 0, scale: 0.8 }}
          transition={{ duration: 0.5 }}
        >
          <div className="card-content">Welcome, {message}!</div>
        </motion.div>
      ) : (
        <motion.div
          key="question"
          initial={{ opacity: 0, scale: 0.8 }}
          animate={{ opacity: 1, scale: 1 }}
          exit={{ opacity: 0, scale: 0.8 }}
          transition={{ duration: 0.5 }}
        >
          <div className="card-content">{message}</div>
        </motion.div>
      )}
    </AnimatePresence>
  );
};

const App = () => {
  const [showWelcome, setShowWelcome] = useState(true);

  // Toggle between welcome and question card
  const toggleCard = () => {
    setShowWelcome((prev) => !prev);
  };

  return (
    <div>
      <CardContainer message="Luna" showWelcome={showWelcome} />
      <button onClick={toggleCard}>Toggle Card</button>
    </div>
  );
};

export default App;
