import React, { useState, useEffect } from "react";
import "../styles/MagicalQuestionCard.css";

const MagicalQuestionCard = ({ question }) => {
  const [show, setShow] = useState(false);

  // Trigger the animation when the question prop changes
  useEffect(() => {
    if (question) {
      setShow(true);
      //setTimeout(() => setShow(false), 2000); // Optional: fade out after 2 seconds
    }
  }, [question]);

  return (
    <div className={`card-container ${show ? "show" : ""}`}>
      <div className="sparkle-background"></div>
      <div className="card-content">
        <h2 className="question-text">{question}</h2>
      </div>
    </div>
  );
};

export default MagicalQuestionCard;
