import React, { useState, useEffect } from "react";
import { motion } from "framer-motion";
import "../styles/MagicalQuestionCard.css";

const generateDustParticles = () => {
  const particles = [];
  for (let i = 0; i < 100; i++) {
    const size = Math.random() * 8 + 3; // Particles are between 3px to 11px
    const randomX = Math.random() * 100; // X position (from 0% to 100%)
    const randomY = Math.random() * 100; // Y position (from 0% to 100%)
    const delay = Math.random() * 1; // Each particle has a different delay for randomness

    particles.push(
      <motion.div
        key={i}
        className="dust-particle"
        initial={{ opacity: 0, x: `${randomX}%`, y: `${randomY}%`, scale: 0 }}
        animate={{ opacity: 1, x: "50%", y: "50%", scale: 1 }}
        transition={{ delay, duration: 1 }}
        style={{
          width: `${size}px`,
          height: `${size}px`,
        }}
      />
    );
  }
  return particles;
};

const MagicalQuestionCard = ({ question, cardKey }) => {
  const [particles, setParticles] = useState([]);

  useEffect(() => {
    setParticles(generateDustParticles());
  }, [cardKey]);

  return (
    <div className="question-card-container">
      <div className="dust-container">{particles}</div>
      <motion.div
        className="question-card"
        initial={{ opacity: 0, scale: 0.5, rotate: 10 }}
        animate={{ opacity: 1, scale: 1, rotate: 0 }}
        transition={{ delay: 1, duration: 1 }}
      >
        <div className="question-content">{question}</div>
      </motion.div>
    </div>
  );
};

export default MagicalQuestionCard;
