// MagicalQuestionCard.js
import React, { useState, useEffect, useRef } from "react";
import "../styles/MagicalQuestionCard.css";

const generateDustParticles = () => {
    const particles = [];
    for (let i = 0; i < 80; i++) {
      const size = Math.random() * 10 + 5;
      const randomX = Math.random() * 200 - 50; // Range: -50% to 150%
      const randomY = Math.random() * 200 - 50; // Range: -50% to 150%
      const delay = Math.random() * 1.5;

      particles.push(
        <div
          key={i}
          className="dust-particle"
          style={{
            left: `${randomX}%`,
            top: `${randomY}%`,
            width: `${size}px`,
            height: `${size}px`,
            animationDelay: `${delay}s`,
          }}
        ></div>
      );
    }
    return particles;
  };

const MagicalQuestionCard = ({ question = "Default Question" }) => {
  const [show, setShow] = useState(false);
  const dustFieldRef = useRef(null);

  useEffect(() => {
    // Trigger show effect for new question
    setShow(true);

    // Ensure the animation plays correctly with new questions. This timeout ensures the particle effect occurs after card animation is visible.
    const timer = setTimeout(() => {
      setShow(false);

       // Clear any previous particles by replacing with new ones. 
       if (dustFieldRef.current){
          dustFieldRef.current.innerHTML = '';
          dustFieldRef.current.appendChild(document.createElement('div'))

      }
    }, 5000);

    return () => clearTimeout(timer); // Cleanup timer on unmount or question change

  }, [question]);

  return (
    <div className="card-wrapper">
      <div className={`card-container ${show ? "show" : ""}`}>
        <div className="dust-field" ref={dustFieldRef}>
         {show && generateDustParticles()}
        </div>
        <div className="card-content">
          <h2 className="question-text">{question}</h2>
        </div>
      </div>
    </div>
  );
};


export default MagicalQuestionCard;
