import React, { useState, useEffect, useCallback } from "react";
import { motion, AnimatePresence } from "framer-motion";
import PropTypes from 'prop-types';
import "../styles/MagicalQuestionCard.css";

const MagicalQuestionCard = ({ question = "Default Question", triggerEffect }) => {
  const [particles, setParticles] = useState([]);
  const [isIntegrating, setIsIntegrating] = useState(true);

  const generateParticles = useCallback(() => {
    // Create a grid of particles that will form the card
    const particlesArray = [];
    const gridSize = { x: 30, y: 20 }; // Adjust for more/less particles

    for (let i = 0; i < gridSize.x; i++) {
      for (let j = 0; j < gridSize.y; j++) {
        particlesArray.push({
          id: `particle-${i}-${j}`,
          initialX: (i / gridSize.x) * 100,
          initialY: (j / gridSize.y) * 100,
          finalX: Math.random() * 200 - 50, // Random scatter position
          finalY: Math.random() * 200 - 50,
          size: Math.random() * 4 + 2,
          delay: Math.random() * 0.5,
          rotation: Math.random() * 360,
          color: `hsl(${Math.random() * 60 + 180}, 70%, 50%)` // Blue-ish colors
        });
      }
    }
    return particlesArray;
  }, []);

  useEffect(() => {
    setIsIntegrating(true);
    setParticles(generateParticles());

    const timer = setTimeout(() => {
      setIsIntegrating(false);
    }, 2000);

    return () => clearTimeout(timer);
  }, [question, triggerEffect, generateParticles]);

  return (
    <div className="thanos-effect-container">
      <AnimatePresence mode="wait">
        <motion.div className="card-base">
          <motion.div 
            className="question-content"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 1.5 }}
          >
            <h2>{question}</h2>
          </motion.div>

          {particles.map((particle) => (
            <motion.div
              key={particle.id}
              className="particle"
              initial={{
                x: isIntegrating ? particle.finalX + '%' : particle.initialX + '%',
                y: isIntegrating ? particle.finalY + '%' : particle.initialY + '%',
                opacity: isIntegrating ? 0 : 1,
                scale: isIntegrating ? 0 : 1,
                rotate: particle.rotation
              }}
              animate={{
                x: isIntegrating ? particle.initialX + '%' : particle.finalX + '%',
                y: isIntegrating ? particle.initialY + '%' : particle.finalY + '%',
                opacity: isIntegrating ? 1 : 0,
                scale: isIntegrating ? 1 : 0
              }}
              transition={{
                duration: 1.5,
                delay: particle.delay,
                ease: "easeInOut"
              }}
              style={{
                width: `${particle.size}px`,
                height: `${particle.size}px`,
                backgroundColor: particle.color,
                position: 'absolute'
              }}
            />
          ))}
        </motion.div>
      </AnimatePresence>
    </div>
  );
};

MagicalQuestionCard.propTypes = {
  question: PropTypes.string,
  triggerEffect: PropTypes.number
};

export default MagicalQuestionCard;
