import React, { useState, useEffect } from "react";
import MagicalQuestionCard from "./MagicalQuestionCard";
import { motion } from "framer-motion";
import useSound from 'use-sound';
import correctSound from '../assets/sounds/correct.mp3';
import wrongSound from '../assets/sounds/wrong.mp3';
import confetti from 'canvas-confetti';

const CATEGORIES = {
  SONG: { name: 'Song', points: 1 },
  MOVIE: { name: 'Movie', points: 2 },
  FAMOUS_PERSON: { name: 'Famous Person', points: 3 },
  FICTIONAL_CHARACTER: { name: 'Fictional Character', points: 3 },
  BOOK: { name: 'Book', points: 4 },
  POET: { name: 'Poet', points: 5 },
  QUOTE: { name: 'Quote', points: 6 }
};

const GamePresenter = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [numberOfRounds, setNumberOfRounds] = useState(5);
  const [gameStarted, setGameStarted] = useState(false);
  const [currentPhrase, setCurrentPhrase] = useState("");
  const [yearGuess, setYearGuess] = useState("");
  const [triggerEffect, setTriggerEffect] = useState(0);
  const [showResult, setShowResult] = useState(null);

  const [playCorrect] = useSound(correctSound);
  const [playWrong] = useSound(wrongSound);

  const startGame = () => {
    setGameStarted(true);
    // Here we'll add the API call to get the first phrase
  };

  const handleCategorySelect = (category) => {
    setSelectedCategory(category);
    setTriggerEffect(prev => prev + 1);
  };

  const handleYearGuess = (e) => {
    setYearGuess(e.target.value);
  };

  const submitGuess = async (answer, year) => {
    const isCorrect = await checkAnswer(answer, currentAnswer, selectedCategory);
    const yearCorrect = parseInt(year) === currentAnswer.year;

    if (isCorrect) {
      playCorrect();
      confetti({
        particleCount: 100,
        spread: 70,
        origin: { y: 0.6 }
      });

      let points = CATEGORIES[selectedCategory].points;
      if (yearCorrect) points *= 2;
      setScore(prev => prev + points);
    } else {
      playWrong();
    }

    // Show result animation
    setShowResult({ correct: isCorrect, yearCorrect });

    // Wait for animation
    setTimeout(() => {
      setCurrentIndex(prev => prev + 1);
      setTriggerEffect(prev => prev + 1);
    }, 2000);
  };

  return (
    <div className="game-container">
      {!gameStarted ? (
        <motion.div 
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          className="game-setup"
        >
          <h1>Legendary Lines Game</h1>
          <div className="rounds-selector">
            <label>Number of Rounds:</label>
            <input 
              type="number" 
              min="1" 
              max="20" 
              value={numberOfRounds}
              onChange={(e) => setNumberOfRounds(parseInt(e.target.value))}
            />
          </div>
          <button onClick={startGame} className="start-btn">
            Start Game
          </button>
        </motion.div>
      ) : (
        <>
          <div className="game-header">
            <h2>Score: {score}</h2>
            <h3>Round {currentIndex + 1} of {numberOfRounds}</h3>
          </div>

          <MagicalQuestionCard 
            question={currentPhrase || "Select a category to begin"} 
            triggerEffect={triggerEffect}
          />

          <div className="categories-grid">
            {Object.entries(CATEGORIES).map(([key, category]) => (
              <motion.button
                key={key}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                className={`category-btn ${selectedCategory === key ? 'selected' : ''}`}
                onClick={() => handleCategorySelect(key)}
              >
                {category.name}
                <span className="points">({category.points} pts)</span>
              </motion.button>
            ))}
          </div>

          {selectedCategory && (
            <motion.div 
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className="guess-section"
            >
              <input
                type="text"
                placeholder="Enter your answer..."
                className="answer-input"
              />
              <div className="year-guess">
                <input
                  type="number"
                  placeholder="Year (optional)"
                  value={yearGuess}
                  onChange={handleYearGuess}
                  className="year-input"
                />
                <span className="bonus-text">Double points for correct year!</span>
              </div>
              <button 
                onClick={() => submitGuess()} 
                className="submit-btn"
              >
                Submit Answer
              </button>
            </motion.div>
          )}
        </>
      )}
    </div>
  );
};

export default GamePresenter;
