import React from 'react';
import { motion, useAnimation } from 'framer-motion';
import './styles/MagicalCard.css';  // Add CSS for dust effect

const MagicalCard = ({ question, onComplete }) => {
  const controls = useAnimation();

  React.useEffect(() => {
    controls.start({
      opacity: 1,
      scale: 1,
      filter: 'blur(0px)',
      transition: {
        duration: 1.5,
        ease: [0.42, 0, 0.58, 1],
      },
    });
  }, [controls]);

  return (
    <motion.div
      initial={{ opacity: 0, scale: 0.8, filter: 'blur(5px)' }}
      animate={controls}
      exit={{ opacity: 0, scale: 0.5, filter: 'blur(10px)' }}
      className="magical-card-container"
    >
      <div className="magical-dust"></div>
      <div className="magical-card-content">
        <h2>{question}</h2>
      </div>
    </motion.div>
  );
};

export default MagicalCard;
