import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';

const integrateAnimation = keyframes`
  0% {
    opacity: 0;
    transform: scale(0.5);
    filter: blur(10px);
  }
  100% {
    opacity: 1;
    transform: scale(1);
    filter: blur(0);
  }
`;

const Card = styled.div`
  background-color: white;
  border-radius: 10px;
  padding: 20px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  animation: ${integrateAnimation} 2s ease-out;
`;

const Particle = styled.div`
  position: absolute;
  width: 5px;
  height: 5px;
  background-color: #f0f0f0;
  border-radius: 50%;
  opacity: 0;
`;

const MagicalQuestionCard = ({ question }) => {
  const [particles, setParticles] = useState([]);

  useEffect(() => {
    const newParticles = [];
    for (let i = 0; i < 100; i++) {
      newParticles.push({
        id: i,
        x: Math.random() * 100,
        y: Math.random() * 100,
        delay: Math.random() * 2,
      });
    }
    setParticles(newParticles);
  }, [question]);

  return (
    <div style={{ position: 'relative', width: '300px', height: '200px' }}>
      {particles.map((particle) => (
        <Particle
          key={particle.id}
          style={{
            left: `${particle.x}%`,
            top: `${particle.y}%`,
            animation: `${integrateAnimation} 2s ease-out ${particle.delay}s forwards`,
          }}
        />
      ))}
      <Card>
        <h3>{question}</h3>
      </Card>
    </div>
  );
};

export default MagicalQuestionCard;
