import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import PropTypes from 'prop-types';
import "../styles/MagicalQuestionCard.css";

const MagicalQuestionCard = ({ question = "Default Question", triggerEffect }) => {
  const [particles, setParticles] = useState([]);
  const [isDisintegrating, setIsDisintegrating] = useState(false);
  const [effectId, setEffectId] = useState(0);

  const generateParticles = () => {
    const particlesArray = [];
    const cardElement = document.querySelector('.card-content');
    if (!cardElement) return [];

    const cardRect = cardElement.getBoundingClientRect();
    const cardWidth = cardRect.width;
    const cardHeight = cardRect.height;

    const columns = 60;
    const rows = 30;

    for (let i = 0; i < columns; i++) {
      for (let j = 0; j < rows; j++) {
        const baseX = (i / columns) * cardWidth;
        const baseY = (j / rows) * cardHeight;

        particlesArray.push({
          id: `particle-${i}-${j}-${effectId}`,
          initialX: baseX,
          initialY: baseY,
          finalX: baseX + (Math.random() - 0.5) * cardWidth * 2,
          finalY: baseY + (Math.random() - 0.5) * cardHeight * 2,
          size: Math.random() * 2 + 1,
          delay: (j / rows) * 0.5 + Math.random() * 0.3,
          rotation: Math.random() * 360
        });
      }
    }
    return particlesArray;
  };

  // Handle initial load
  useEffect(() => {
    setParticles(generateParticles());
  }, []);

  // Handle question changes
  useEffect(() => {
    // Skip the initial render
    if (triggerEffect === 0) return;

    // Start disintegration
    setIsDisintegrating(true);
    setEffectId(prev => prev + 1);

    // Wait for disintegration animation
    const disintegrationTimer = setTimeout(() => {
      setParticles([]); // Clear current particles

      // Generate new particles and start integration
      const integrationTimer = setTimeout(() => {
        setIsDisintegrating(false);
        setParticles(generateParticles());
      }, 100);

      return () => clearTimeout(integrationTimer);
    }, 1500);

    return () => clearTimeout(disintegrationTimer);
  }, [triggerEffect]);

  return (
    <div className="card-container">
      <div className="card-wrapper">
        <motion.div 
          className="card-content"
          key={`content-${triggerEffect}`}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          <motion.h2 
            className="question-text"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.5 }}
          >
            {question}
          </motion.h2>
        </motion.div>

        <div className="particles-wrapper">
          <AnimatePresence mode="wait">
            {particles.map((particle) => (
              <motion.div
                key={particle.id}
                className="thanos-particle"
                initial={{
                  x: isDisintegrating ? particle.initialX : particle.finalX,
                  y: isDisintegrating ? particle.initialY : particle.finalY,
                  opacity: 0,
                  scale: 0,
                  rotate: particle.rotation
                }}
                animate={{
                  x: isDisintegrating ? particle.finalX : particle.initialX,
                  y: isDisintegrating ? particle.finalY : particle.initialY,
                  opacity: isDisintegrating ? 0 : 0.8,
                  scale: isDisintegrating ? 0 : 1,
                  rotate: isDisintegrating ? particle.rotation : 0
                }}
                exit={{ 
                  opacity: 0, 
                  scale: 0,
                  transition: { duration: 0.5 }
                }}
                transition={{
                  duration: 1,
                  delay: particle.delay,
                  ease: [0.23, 1, 0.32, 1]
                }}
                style={{
                  width: `${particle.size}px`,
                  height: `${particle.size}px`,
                  position: 'absolute',
                  left: 0,
                  top: 0,
                  background: '#4FD1C5',
                  borderRadius: '50%',
                  boxShadow: '0 0 4px rgba(79, 209, 197, 0.6)'
                }}
              />
            ))}
          </AnimatePresence>
        </div>
      </div>
    </div>
  );
};

MagicalQuestionCard.propTypes = {
  question: PropTypes.string,
  triggerEffect: PropTypes.number
};

export default MagicalQuestionCard;
