import React, { useState } from 'react';
import MagicalQuestionCard from './MagicalQuestionCard';
import '../styles/Game.css';

const Game = () => {
  const [question, setQuestion] = useState('What is the capital of France?');
  const [questionKey, setQuestionKey] = useState(0);

  const generateNewQuestion = () => {
    // Simulate AI-generated question
    const questions = [
      'What is the capital of France?',
      'What is the largest planet in our solar system?',
      'Who wrote "To Kill a Mockingbird"?',
      'What is the speed of light?',
      'Who painted the Mona Lisa?',
    ];
    const randomQuestion = questions[Math.floor(Math.random() * questions.length)];
    setQuestion(randomQuestion);
    setQuestionKey(prevKey => prevKey + 1);
  };

  return (
    <div className="game-container">
      <MagicalQuestionCard question={question} cardKey={questionKey} />
      <button onClick={generateNewQuestion}>Next Question</button>
    </div>
  );
};

export default Game;
