import React, { useState } from "react";
import GamePresenter from "./components/GamePresenter";
import QuestionCard from "./components/MagicalQuestionCard";
import ImageCarousel from "./components/ImageCarousel";

function App() {
  const [customQuestion, setCustomQuestion] = useState("");

  const handleCustomQuestion = () => {
    setCustomQuestion("This is a special custom question!");
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Magical Quiz Game</h1>
        <ImageCarousel />
      </header>

      {/* GamePresenter handles the quiz functionality */}
      <GamePresenter />

      {/* Example of standalone QuestionCard */}
      <div style={{ marginTop: "40px" }}>
        <h2>Special Question Card:</h2>
        <QuestionCard question={customQuestion || "No custom question yet"} />
        <button
          style={{ marginTop: "20px" }}
          onClick={handleCustomQuestion}
          className="next-btn"
        >
          Show Custom Question
        </button>
      </div>
    </div>
  );
}

export default App;
