import React from 'react';
import Particles from 'react-tsparticles';
import { loadFull } from 'tsparticles';

const MagicalCard = ({ question }) => {
  const particlesInit = async (engine) => {
    await loadFull(engine);
  };

  return (
    <div className="magical-card-container" style={{ position: 'relative', width: '300px', height: '200px' }}>
      {/* Particle Animation */}
      <Particles
        id="tsparticles"
        init={particlesInit}
        options={{
          fullScreen: false,
          particles: {
            number: {
              value: 100,
              density: { enable: true, value_area: 800 },
            },
            color: { value: "#ffffff" },
            shape: { type: "circle" },
            opacity: {
              value: 0.5,
              random: true,
              anim: { enable: true, speed: 1, opacity_min: 0.1, sync: false },
            },
            size: {
              value: 3,
              random: true,
              anim: { enable: true, speed: 40, size_min: 0.1, sync: false },
            },
            line_linked: { enable: false },
            move: {
              enable: true,
              speed: 3,
              direction: "none",
              random: false,
              straight: false,
              out_mode: "out",
              bounce: false,
              attract: { enable: false, rotateX: 600, rotateY: 1200 },
            },
          },
          interactivity: {
            detect_on: "canvas",
            events: {
              onhover: { enable: false },
              onclick: { enable: false },
              resize: true,
            },
          },
          retina_detect: true,
        }}
      />
      {/* Card Content */}
      <div className="magical-card-content" style={{ position: 'absolute', top: 0, left: 0, width: '100%', height: '100%' }}>
        <h2>{question}</h2>
      </div>
    </div>
  );
};

export default MagicalCard;
