import React, { useState, useEffect } from "react";
import MagicalQuestionCard from "./MagicalQuestionCard";
import { motion, AnimatePresence } from "framer-motion";
import useSound from 'use-sound';
import correctSound from '../assets/sounds/correct.mp3';
import wrongSound from '../assets/sounds/wrong.mp3';
import confetti from 'canvas-confetti';
import { getPhrase, checkAnswer, handleAPIError } from '../services/aiService';

const CATEGORIES = {
  SONG: { name: 'Song', points: 1 },
  MOVIE: { name: 'Movie', points: 2 },
  FAMOUS_PERSON: { name: 'Famous Person', points: 3 },
  FICTIONAL_CHARACTER: { name: 'Fictional Character', points: 3 },
  BOOK: { name: 'Book', points: 4 },
  POET: { name: 'Poet', points: 5 },
  QUOTE: { name: 'Quote', points: 6 }
};

const DIFFICULTIES = {
  EASY: { 
    color: '#4CAF50', 
    multiplier: 1,
    particles: {
      particleCount: 50,
      spread: 45,
      colors: ['#4CAF50', '#81C784']
    },
    animation: {
      scale: 1.05,
      transition: { duration: 0.3 }
    }
  },
  MEDIUM: { 
    color: '#FFC107', 
    multiplier: 1.5,
    particles: {
      particleCount: 100,
      spread: 70,
      colors: ['#FFC107', '#FFD54F']
    },
    animation: {
      scale: 1.1,
      rotate: [0, 5, -5, 0],
      transition: { duration: 0.5 }
    }
  },
  HARD: { 
    color: '#F44336', 
    multiplier: 2,
    particles: {
      particleCount: 150,
      spread: 100,
      colors: ['#F44336', '#EF5350'],
      startVelocity: 45
    },
    animation: {
      scale: 1.15,
      rotate: [0, 10, -10, 0],
      transition: { duration: 0.7 }
    }
  }
};

// Add achievement effects configuration
const ACHIEVEMENT_EFFECTS = {
  STREAK_3: {
    particles: {
      particleCount: 30,
      spread: 60,
      colors: ['#FFD700'],
      shapes: ['star']
    },
    sound: 'streak3Sound'
  },
  STREAK_5: {
    particles: {
      particleCount: 50,
      spread: 90,
      colors: ['#FFD700', '#FFA500'],
      shapes: ['star', 'circle']
    },
    sound: 'streak5Sound'
  },
  DIFFICULTY_UP: {
    particles: {
      particleCount: 100,
      spread: 120,
      colors: ['#4CAF50', '#81C784', '#C8E6C9'],
      shapes: ['star', 'circle', 'square']
    },
    sound: 'levelUpSound'
  }
};

const GamePresenter = () => {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [score, setScore] = useState(0);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [numberOfRounds, setNumberOfRounds] = useState(5);
  const [gameStarted, setGameStarted] = useState(false);
  const [currentPhrase, setCurrentPhrase] = useState(null);
  const [yearGuess, setYearGuess] = useState("");
  const [triggerEffect, setTriggerEffect] = useState(0);
  const [showResult, setShowResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [difficulty, setDifficulty] = useState('MEDIUM');
  const [retryCount, setRetryCount] = useState(0);
  const [errorMessage, setErrorMessage] = useState(null);
  const [progressiveScore, setProgressiveScore] = useState(0);
  const [streakCount, setStreakCount] = useState(0);
  const [errorRetries, setErrorRetries] = useState({});
  const [difficultyHistory, setDifficultyHistory] = useState([]);
  const [showDifficultyChange, setShowDifficultyChange] = useState(false);
  const [achievements, setAchievements] = useState([]);
  const [showStats, setShowStats] = useState(false);
  const [streakHistory, setStreakHistory] = useState([]);

  const DIFFICULTY_THRESHOLDS = {
    MEDIUM: {
      score: 25,
      streak: 2,
      consistency: 0.7 // 70% correct answers
    },
    HARD: {
      score: 50,
      streak: 3,
      consistency: 0.8 // 80% correct answers
    }
  };

  const [playCorrect] = useSound(correctSound);
  const [playWrong] = useSound(wrongSound);

  const startGame = () => {
    setGameStarted(true);
    // Here we'll add the API call to get the first phrase
  };

  const handleError = async (error, action) => {
    const errorKey = `${action}-${Date.now()}`;
    const currentRetries = errorRetries[errorKey] || 0;

    try {
      const { shouldRetry, delay } = await handleAPIError(error, currentRetries);

      if (shouldRetry) {
        setErrorRetries(prev => ({
          ...prev,
          [errorKey]: currentRetries + 1
        }));

        // Show retry message
        setErrorMessage(`Retrying... Attempt ${currentRetries + 1}/3`);

        // Retry the action
        setTimeout(() => {
          if (action === 'fetch') {
            handleCategorySelect(selectedCategory);
          } else if (action === 'submit') {
            submitGuess();
          }
        }, delay);
      }
    } catch (finalError) {
      setErrorMessage(finalError.message);
      // Clear retry count for this error
      setErrorRetries(prev => {
        const newRetries = { ...prev };
        delete newRetries[errorKey];
        return newRetries;
      });
    }
  };

  const handleCategorySelect = async (category) => {
    setLoading(true);
    setErrorMessage(null);

    try {
      const phraseData = await getPhrase(category, difficulty);
      setCurrentPhrase(phraseData);
      setSelectedCategory(category);
      setTriggerEffect(prev => prev + 1);
      setRetryCount(0); // Reset retry count on success
    } catch (error) {
      handleError(error, 'fetch phrase');
    } finally {
      setLoading(false);
    }
  };

  const handleYearGuess = (e) => {
    setYearGuess(e.target.value);
  };

  const submitGuess = async (userAnswer, yearGuess) => {
    setLoading(true);
    setError(null);
    try {
      const result = await checkAnswer(userAnswer, currentPhrase, selectedCategory);

      if (result.isCorrect) {
        playCorrect();
        confetti({
          particleCount: 100,
          spread: 70,
          origin: { y: 0.6 }
        });

        // Calculate points
        let points = CATEGORIES[selectedCategory].points;
        if (parseInt(yearGuess) === currentPhrase.year) {
          points *= 2; // Double points for correct year
        }
        setScore(prev => prev + points);

        // Show success message
        setShowResult({
          correct: true,
          message: result.explanation,
          similarity: result.similarity
        });
      } else {
        playWrong();
        setShowResult({
          correct: false,
          message: result.explanation,
          similarity: result.similarity
        });
      }

    } catch (err) {
      setError('Failed to check answer. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const calculatePoints = (result, yearCorrect) => {
    const basePoints = CATEGORIES[selectedCategory].points;
    const difficultyMultiplier = DIFFICULTIES[difficulty].multiplier;
    let points = basePoints * difficultyMultiplier;

    // Add partial credit if available
    if (result.partialCredit && result.partialCredit > 0) {
      points *= (result.partialCredit / 100);
    }

    // Double points for correct year
    if (yearCorrect) points *= 2;

    return Math.round(points);
  };

  const triggerDifficultyEffect = (difficulty, correct) => {
    if (!correct) return;

    const config = DIFFICULTIES[difficulty].particles;
    confetti({
      ...config,
      origin: { y: 0.6 },
      gravity: 1.5,
      scalar: 1.2,
      ticks: 200
    });
  };

  const calculateConsistency = () => {
    const recentAnswers = difficultyHistory.slice(-10);
    if (recentAnswers.length < 5) return 0;
    return recentAnswers.filter(x => x).length / recentAnswers.length;
  };

  const updateDifficultyBasedOnProgress = () => {
    const consistency = calculateConsistency();
    const currentLevel = difficulty;
    let newLevel = currentLevel;

    if (streakCount >= DIFFICULTY_THRESHOLDS.HARD.streak && 
        progressiveScore >= DIFFICULTY_THRESHOLDS.HARD.score &&
        consistency >= DIFFICULTY_THRESHOLDS.HARD.consistency) {
      newLevel = 'HARD';
    } else if (streakCount >= DIFFICULTY_THRESHOLDS.MEDIUM.streak &&
               progressiveScore >= DIFFICULTY_THRESHOLDS.MEDIUM.score &&
               consistency >= DIFFICULTY_THRESHOLDS.MEDIUM.consistency) {
      newLevel = 'MEDIUM';
    }

    if (newLevel !== currentLevel) {
      setDifficulty(newLevel);
      showDifficultyChangeNotification(newLevel);
    }
  };

  const showDifficultyChangeNotification = (newLevel) => {
    setShowDifficultyChange(true);
    setTimeout(() => setShowDifficultyChange(false), 2000);
  };

  const handleCorrectAnswer = (points) => {
    setStreakCount(prev => prev + 1);
    setProgressiveScore(prev => prev + points);
    setDifficultyHistory(prev => [...prev, true]);
    updateDifficultyBasedOnProgress();

    // Trigger appropriate difficulty effect
    triggerDifficultyEffect(difficulty, true);
  };

  const handleWrongAnswer = () => {
    setStreakCount(0);
    setDifficultyHistory(prev => [...prev, false]);
    setProgressiveScore(prev => Math.max(0, prev - 5));
  };

  // Add visual feedback for progressive difficulty
  const renderProgressiveIndicator = () => (
    <motion.div 
      className="progressive-difficulty"
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
    >
      <div className="difficulty-bar">
        <div 
          className="difficulty-fill"
          style={{ 
            width: `${(progressiveScore / DIFFICULTY_THRESHOLDS.HARD.score) * 100}%`,
            backgroundColor: DIFFICULTIES[difficulty].color
          }}
          animate={DIFFICULTIES[difficulty].animation}
        />
      </div>
      <motion.div 
        className={`streak-counter ${streakCount > 0 ? 'streak-pulse' : ''}`}
        animate={{ scale: streakCount > 0 ? [1, 1.1, 1] : 1 }}
      >
        Streak: {streakCount}
        {streakCount >= 3 && (
          <span className="streak-fire">ðŸ”¥</span>
        )}
      </motion.div>
      <div className="current-difficulty">
        Level: {difficulty}
      </div>
    </motion.div>
  );

  const triggerAchievementEffect = (type) => {
    const effect = ACHIEVEMENT_EFFECTS[type];
    confetti({
      ...effect.particles,
      origin: { y: 0.6 },
      gravity: 1.2,
      scalar: 1.2,
      ticks: 150
    });
  };

  const updateStreakHistory = (correct) => {
    const newHistory = [...streakHistory, correct];
    setStreakHistory(newHistory.slice(-20)); // Keep last 20 attempts

    // Check for streak achievements
    if (streakCount === 3) {
      triggerAchievementEffect('STREAK_3');
      addAchievement('3 Streak!');
    } else if (streakCount === 5) {
      triggerAchievementEffect('STREAK_5');
      addAchievement('5 Streak! Amazing!');
    }
  };

  const addAchievement = (message) => {
    setAchievements(prev => [...prev, { message, timestamp: Date.now() }]);
    setTimeout(() => {
      setAchievements(prev => prev.slice(1));
    }, 3000);
  };

  return (
    <div className="game-container">
      {!gameStarted ? (
        <motion.div 
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          className="game-setup"
        >
          <h1>Legendary Lines Game</h1>

          {/* Difficulty Selection */}
          <div className="difficulty-selector">
            <h3>Select Difficulty:</h3>
            <div className="difficulty-buttons">
              {Object.keys(DIFFICULTIES).map(diff => (
                <motion.button
                  key={diff}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  className={`difficulty-btn ${difficulty === diff ? 'selected' : ''}`}
                  style={{ 
                    backgroundColor: DIFFICULTIES[diff].color,
                    opacity: difficulty === diff ? 1 : 0.7 
                  }}
                  onClick={() => setDifficulty(diff)}
                >
                  {diff}
                  <span className="multiplier">
                    {DIFFICULTIES[diff].multiplier}x points
                  </span>
                </motion.button>
              ))}
            </div>
          </div>

          <div className="rounds-selector">
            <label>Number of Rounds:</label>
            <input 
              type="number" 
              min="1" 
              max="20" 
              value={numberOfRounds}
              onChange={(e) => setNumberOfRounds(parseInt(e.target.value))}
            />
          </div>
          <button onClick={startGame} className="start-btn">
            Start Game
          </button>
        </motion.div>
      ) : (
        <>
          <div className="game-header">
            <h2>Score: {score}</h2>
            <h3>Round {currentIndex + 1} of {numberOfRounds}</h3>
          </div>

          <MagicalQuestionCard 
            question={currentPhrase?.phrase || "Select a category to begin"} 
            triggerEffect={triggerEffect}
            loading={loading}
          />

          <div className="categories-grid">
            {Object.entries(CATEGORIES).map(([key, category]) => (
              <motion.button
                key={key}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                className={`category-btn ${selectedCategory === key ? 'selected' : ''}`}
                onClick={() => handleCategorySelect(key)}
              >
                {category.name}
                <span className="points">({category.points} pts)</span>
              </motion.button>
            ))}
          </div>

          {selectedCategory && (
            <motion.div 
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              className="guess-section"
            >
              <input
                type="text"
                placeholder="Enter your answer..."
                className="answer-input"
              />
              <div className="year-guess">
                <input
                  type="number"
                  placeholder="Year (optional)"
                  value={yearGuess}
                  onChange={handleYearGuess}
                  className="year-input"
                />
                <span className="bonus-text">Double points for correct year!</span>
              </div>
              <button 
                onClick={() => submitGuess()} 
                className="submit-btn"
              >
                Submit Answer
              </button>
            </motion.div>
          )}

          {/* Show loading state */}
          {loading && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className="loading-spinner"
            >
              Loading...
            </motion.div>
          )}

          {/* Show error state */}
          {error && (
            <motion.div
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              className="error-message"
            >
              {error}
            </motion.div>
          )}
        </>
      )}

      {renderProgressiveIndicator()}

      <AnimatePresence>
        {errorMessage && (
          <motion.div
            className="error-message"
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
          >
            {errorMessage}
            <button onClick={() => setErrorMessage(null)}>
              Dismiss
            </button>
          </motion.div>
        )}
      </AnimatePresence>

      <AnimatePresence>
        {showDifficultyChange && (
          <motion.div
            className="difficulty-change"
            initial={{ scale: 0, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            exit={{ scale: 0, opacity: 0 }}
            style={{ color: DIFFICULTIES[difficulty].color }}
          >
            Difficulty Increased to {difficulty}!
          </motion.div>
        )}
      </AnimatePresence>

      {/* Streak and Progress Visualization */}
      <motion.div 
        className="streak-visualizer"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
      >
        {streakHistory.map((correct, index) => (
          <motion.div
            key={index}
            className={`streak-dot ${correct ? 'correct' : 'incorrect'}`}
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            transition={{ delay: index * 0.1 }}
          />
        ))}
      </motion.div>

      {/* Achievements Display */}
      <AnimatePresence>
        {achievements.map((achievement, index) => (
          <motion.div
            key={achievement.timestamp}
            className="achievement-notification"
            initial={{ x: 100, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            exit={{ x: -100, opacity: 0 }}
            style={{ top: `${120 + index * 70}px` }}
          >
            {achievement.message}
          </motion.div>
        ))}
      </AnimatePresence>

      {/* Stats Panel */}
      <AnimatePresence>
        {showStats && (
          <motion.div
            className="stats-panel"
            initial={{ opacity: 0, y: 50 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: 50 }}
          >
            <h3>Progress Statistics</h3>
            <div className="stats-grid">
              <div className="stat-item">
                <span className="stat-label">Best Streak</span>
                <span className="stat-value">{Math.max(...streakHistory)}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Accuracy</span>
                <span className="stat-value">
                  {Math.round((streakHistory.filter(x => x).length / streakHistory.length) * 100)}%
                </span>
              </div>
              <div className="stat-item">
                <span className="stat-label">Current Level</span>
                <span className="stat-value">{difficulty}</span>
              </div>
            </div>
            <button 
              className="close-stats"
              onClick={() => setShowStats(false)}
            >
              Close
            </button>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default GamePresenter;
