import React, { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import PropTypes from 'prop-types';
import "../styles/MagicalQuestionCard.css";

const MagicalQuestionCard = ({ question = "Default Question", triggerEffect }) => {
  const [particles, setParticles] = useState([]);
  const [showParticles, setShowParticles] = useState(true);

  const generateParticles = () => {
    const particlesArray = [];
    const cardElement = document.querySelector('.card-content');
    if (!cardElement) return [];

    const cardRect = cardElement.getBoundingClientRect();
    const cardWidth = cardRect.width;
    const cardHeight = cardRect.height;

    const columns = 60;
    const rows = 30;

    for (let i = 0; i < columns; i++) {
      for (let j = 0; j < rows; j++) {
        const baseX = (i / columns) * cardWidth;
        const baseY = (j / rows) * cardHeight;

        particlesArray.push({
          id: `particle-${i}-${j}-${Date.now()}`,
          initialX: baseX,
          initialY: baseY,
          finalX: baseX + (Math.random() - 0.5) * cardWidth * 2,
          finalY: baseY + (Math.random() - 0.5) * cardHeight * 2,
          size: Math.random() * 2 + 1,
          delay: (j / rows) * 0.5 + Math.random() * 0.3,
          rotation: Math.random() * 360,
          opacity: Math.random() * 0.5 + 0.5
        });
      }
    }
    return particlesArray;
  };

  useEffect(() => {
    if (triggerEffect === 0) {
      // Initial load
      setParticles(generateParticles());
      setShowParticles(true);
    } else {
      // Start vanishing animation
      setShowParticles(false);

      // Wait for vanishing animation to complete
      const timer = setTimeout(() => {
        setParticles(generateParticles());
        setShowParticles(true);
      }, 1500);

      return () => clearTimeout(timer);
    }
  }, [triggerEffect]);

  return (
    <div className="card-container">
      <div className="card-wrapper">
        <motion.div 
          className="card-content"
          key={`content-${triggerEffect}`}
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          <motion.h2 
            className="question-text"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 0.5 }}
          >
            {question}
          </motion.h2>
        </motion.div>

        <div className="particles-wrapper">
          <AnimatePresence mode="wait">
            {showParticles && (
              <motion.div
                key={`particles-container-${triggerEffect}`}
                exit={{ opacity: 0 }}
              >
                {particles.map((particle) => (
                  <motion.div
                    key={particle.id}
                    className="thanos-particle"
                    initial={{
                      x: particle.finalX,
                      y: particle.finalY,
                      opacity: 0,
                      scale: 0,
                      rotate: particle.rotation
                    }}
                    animate={{
                      x: particle.initialX,
                      y: particle.initialY,
                      opacity: particle.opacity,
                      scale: 1,
                      rotate: 0
                    }}
                    exit={{
                      x: particle.finalX,
                      y: particle.finalY,
                      opacity: 0,
                      scale: 0,
                      rotate: particle.rotation
                    }}
                    transition={{
                      duration: 1.5,
                      delay: particle.delay,
                      ease: [0.23, 1, 0.32, 1]
                    }}
                    style={{
                      width: `${particle.size}px`,
                      height: `${particle.size}px`,
                      position: 'absolute',
                      left: 0,
                      top: 0,
                      background: '#4FD1C5',
                      borderRadius: '50%',
                      boxShadow: '0 0 4px rgba(79, 209, 197, 0.6)',
                      zIndex: 2
                    }}
                  />
                ))}
              </motion.div>
            )}
          </AnimatePresence>
        </div>
      </div>
    </div>
  );
};

MagicalQuestionCard.propTypes = {
  question: PropTypes.string,
  triggerEffect: PropTypes.number
};

export default MagicalQuestionCard;
