import React, { useEffect, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import '../styles/MagicalQuestionCard.css';

const MagicalQuestionCard = ({ message, triggerEffect }) => {
  const cardRef = useRef(null);

  useEffect(() => {
    if (cardRef.current) {
      createParticles(cardRef.current);
    }
  }, [message, triggerEffect]);

  const createParticles = (element) => {
    // Clear existing particles
    const existingWrapper = element.querySelector('.particles-wrapper');
    if (existingWrapper) {
      existingWrapper.remove();
    }

    // Create particles wrapper
    const particlesWrapper = document.createElement('div');
    particlesWrapper.className = 'particles-wrapper';

    // Calculate text bounds to concentrate particles around the text
    const textElement = element.querySelector('.card-content');
    const textBounds = textElement.getBoundingClientRect();
    const elementBounds = element.getBoundingClientRect();

    // Create new particles
    for (let i = 0; i < 500; i++) { // Increased number of particles
      const particle = document.createElement('div');
      particle.className = 'thanos-particle';

      // Concentrate particles around the text area
      const padding = 10;
      const x = (textBounds.left - elementBounds.left) + 
                padding + (Math.random() * (textBounds.width - 2 * padding));
      const y = (textBounds.top - elementBounds.top) + 
                padding + (Math.random() * (textBounds.height - 2 * padding));

      // Vary particle sizes more dramatically
      const size = Math.random() * 4 + 2; // Larger particles (2-6px)
      particle.style.width = `${size}px`;
      particle.style.height = `${size}px`;

      // Set final position (where particles will form the message)
      particle.style.left = `${x}px`;
      particle.style.top = `${y}px`;

      // Set initial position (particles come from further away)
      const angle = Math.random() * Math.PI * 2;
      const distance = 150 + Math.random() * 100; // Particles come from further away
      const tx = Math.cos(angle) * distance;
      const ty = Math.sin(angle) * distance;
      particle.style.setProperty('--tx', `${tx}px`);
      particle.style.setProperty('--ty', `${ty}px`);

      // Enhanced animation timing
      const inDelay = Math.random() * 0.5; // Slightly longer in-animation
      const holdDelay = 1.5; // Time to hold the formed message
      const outDelay = holdDelay + Math.random() * 0.3; // Delay before disintegrating

      particle.style.animation = `
        particleAnimationIn 1s cubic-bezier(0.4, 0, 0.2, 1) ${inDelay}s forwards,
        particleAnimationOut 1s cubic-bezier(0.4, 0, 0.2, 1) ${outDelay}s forwards
      `;

      particlesWrapper.appendChild(particle);
    }

    element.insertBefore(particlesWrapper, element.firstChild);
  };

  function handleSubmit(event) {
    event.preventDefault();
    const inputValue = event.target.elements.inputName.value; // Ensure this is correct
    // Check if inputValue is being sent correctly
    if (inputValue) {
        // Send inputValue to the AI
        sendToAI(inputValue);
    } else {
        console.error("Input value is empty");
    }
  }

  return (
    <AnimatePresence>
      <motion.div 
        ref={cardRef}
        className="magical-card"
        key={triggerEffect}
        initial={{ opacity: 0, scale: 0.8 }}
        animate={{ opacity: 1, scale: 1 }}
        transition={{ duration: 0.5 }}
      >
        <div className="card-content">
          {message || "Waiting for the game to begin..."}
        </div>
      </motion.div>
    </AnimatePresence>
  );
};

export default MagicalQuestionCard;
