import React, { useState, useEffect, useCallback } from "react";
import { motion, AnimatePresence } from "framer-motion";
import PropTypes from 'prop-types';
import "../styles/MagicalQuestionCard.css";

const MagicalQuestionCard = ({ question = "Default Question", triggerEffect }) => {
  const [particles, setParticles] = useState([]);
  const [isIntegrating, setIsIntegrating] = useState(true);
  const [key, setKey] = useState(0);

  const generateParticles = useCallback(() => {
    const particlesArray = [];
    const cardWidth = 600;
    const cardHeight = 300;

    const columns = 60;
    const rows = 30;
    const cellWidth = cardWidth / columns;
    const cellHeight = cardHeight / rows;

    for (let i = 0; i < columns; i++) {
      for (let j = 0; j < rows; j++) {
        const baseX = (i / columns) * cardWidth;
        const baseY = (j / rows) * cardHeight;

        const particlesPerCell = 2;
        for (let k = 0; k < particlesPerCell; k++) {
          const randomOffsetX = Math.random() * cellWidth * 0.8;
          const randomOffsetY = Math.random() * cellHeight * 0.8;

          particlesArray.push({
            id: `particle-${i}-${j}-${k}-${key}`,
            initialX: baseX + randomOffsetX,
            initialY: baseY + randomOffsetY,
            finalX: baseX + (Math.random() - 0.5) * cardWidth * 1.5,
            finalY: baseY - Math.random() * cardHeight * 2,
            size: Math.random() * 2 + 1,
            delay: (j / rows) * 0.8 + Math.random() * 0.3,
            opacity: Math.random() * 0.5 + 0.5,
            rotation: Math.random() * 360
          });
        }
      }
    }
    return particlesArray;
  }, [key]);

  useEffect(() => {
    setIsIntegrating(true);
    setParticles(generateParticles());

    const integrationTimer = setTimeout(() => {
      setIsIntegrating(false);
    }, 2500);

    return () => clearTimeout(integrationTimer);
  }, [generateParticles, triggerEffect]);

  useEffect(() => {
    if (triggerEffect > 0) {
      setKey(prev => prev + 1);
    }
  }, [triggerEffect]);

  return (
    <div className="card-container">
      <div className="card-wrapper">
        <motion.div 
          className="card-content"
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          <motion.h2 
            className="question-text"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ delay: 1.2 }}
          >
            {question}
          </motion.h2>
        </motion.div>

        <div className="particles-wrapper">
          <AnimatePresence mode="wait">
            {particles.map((particle) => (
              <motion.div
                key={particle.id}
                className="thanos-particle"
                initial={{
                  x: isIntegrating ? particle.finalX : particle.initialX,
                  y: isIntegrating ? particle.finalY : particle.initialY,
                  opacity: 0,
                  scale: 0,
                  rotate: particle.rotation
                }}
                animate={{
                  x: isIntegrating ? particle.initialX : particle.finalX,
                  y: isIntegrating ? particle.initialY : particle.finalY,
                  opacity: isIntegrating ? particle.opacity : 0,
                  scale: isIntegrating ? 1 : 0,
                  rotate: isIntegrating ? 0 : particle.rotation
                }}
                exit={{
                  x: particle.finalX,
                  y: particle.finalY,
                  opacity: 0,
                  scale: 0,
                  rotate: particle.rotation
                }}
                transition={{
                  duration: 1.8,
                  delay: particle.delay,
                  ease: [0.23, 1, 0.32, 1]
                }}
                style={{
                  width: `${particle.size}px`,
                  height: `${particle.size}px`,
                  position: 'absolute',
                  left: 0,
                  top: 0,
                  transform: `translate(${particle.initialX}px, ${particle.initialY}px)`
                }}
              />
            ))}
          </AnimatePresence>
        </div>
      </div>
    </div>
  );
};

MagicalQuestionCard.propTypes = {
  question: PropTypes.string,
  triggerEffect: PropTypes.number
};

export default MagicalQuestionCard;
