import '../styles/MagicalQuestionCard.css';

const MagicalQuestionCard = ({ phrase, triggerEffect }) => {
  const containerRef = useRef(null);

  useEffect(() => {
    // ... existing particle logic ...
  }, [triggerEffect]);

  return (
    <div className="card-container">
      <div className="card-content">
        <div className="question-content">
          <h2 className="question-text">{phrase || 'Default Question'}</h2>
        </div>
      </div>
      <div ref={containerRef} className="particles-container" />
    </div>
  );
};

export default MagicalQuestionCard;
